def add_random_edge(v_mat, ass):
    n = v_mat.shape[0]
    n_range = torch.arange(n)
    while True:
        i = random.sample(range(n), 1)[0]
        indexes = n_range[(ass != ass[i]) & (v_mat[i] == 0)]
        j = torch.permute(indexes, dims=(0,))
        if len(j) == 0: continue
        j = j[0]
        print("adding edge", i, j)
        v_mat[i, j] = v_mat[j, i] = 1
        break


