    def random_graph(self):
        return self.get_one(random.choice(self._gp))
    @staticmethod
    def get_one(g):
        # load graph
        jg = json.load(open(g))
        mat = torch.Tensor(jg['m']).reshape([jg['v'], jg['v']])
        mat_adv = mat.clone()
        cc = jg['change_coord']
        mat_adv[cc[0], cc[1]] = 1
        mat_adv[cc[1], cc[0]] = 1
        return mat, mat_adv, jg['c_number']


